from Crypto.Util.number import getPrime
from secret import FLAG

class BeefSoupEncryption:
	# Beef Soup Encryption setup
	def __init__(self):
		self.beef = getPrime(1024)
		self.soup = getPrime(1024)
		self.delicious = 65536
		self.beefsoup = self.beef * self.soup

	def encrypt(self, message):
		encrypted_message = [pow(ord(m), self.delicious, self.beefsoup) for m in message]
		return encrypted_message

	def getHint(self):
		print(f"Here's your hint: {encrypted_flag}\nBtw, the beefsoup: {self.beefsoup}")

if __name__ == "__main__":
	beefSoupEncryption = BeefSoupEncryption()

	# Super secure encrypted flag
	encrypted_flag = beefSoupEncryption.encrypt(FLAG)

	# Output
	beefSoupEncryption.getHint()

